import { useState } from "react"
import { motion, AnimatePresence } from "framer-motion"

export default function ModelApplicationForm() {
  const [step, setStep] = useState(1)
  const [showToast, setShowToast] = useState(false)
  const totalSteps = 10

  // Image states for all categories
  const [headshots, setHeadshots] = useState([null, null, null])
  const [closeUps, setCloseUps] = useState([null, null, null])
  const [fullBody, setFullBody] = useState([null, null, null])
  const [pants, setPants] = useState([null, null, null])
  const [sportswear, setSportswear] = useState([null, null, null])

  const handleSubmit = (e) => {
    e.preventDefault()
    setShowToast(true)
    setTimeout(() => setShowToast(false), 3000)
  }

  const handleUpload = (event, setter, index) => {
    const file = event.target.files[0]
    if (!file) return
    const url = URL.createObjectURL(file)
    setter(prev => prev.map((img, i) => (i === index ? url : img)))
  }

  const handleRemove = (setter, index) => {
    setter(prev => prev.map((img, i) => (i === index ? null : img)))
  }

  // Placeholder component with stock images, remove button, and click-to-upload
  const ImagePlaceholders = ({ images, setter, category }) => {
    const defaultImages = {
      headshots: [
        "https://images.pexels.com/photos/415829/pexels-photo-415829.jpeg",
        "https://images.pexels.com/photos/2379005/pexels-photo-2379005.jpeg",
        "https://images.pexels.com/photos/91227/pexels-photo-91227.jpeg"
      ],
      closeUps: [
        "https://images.pexels.com/photos/733872/pexels-photo-733872.jpeg",
        "https://images.pexels.com/photos/3778613/pexels-photo-3778613.jpeg",
        "https://images.pexels.com/photos/3778680/pexels-photo-3778680.jpeg"
      ],
      fullBody: [
        "https://images.pexels.com/photos/428338/pexels-photo-428338.jpeg",
        "https://images.pexels.com/photos/614810/pexels-photo-614810.jpeg",
        "https://images.pexels.com/photos/774909/pexels-photo-774909.jpeg"
      ],
      pants: [
        "https://images.pexels.com/photos/2529148/pexels-photo-2529148.jpeg",
        "https://images.pexels.com/photos/2983464/pexels-photo-2983464.jpeg",
        "https://images.pexels.com/photos/3258762/pexels-photo-3258762.jpeg"
      ],
      sportswear: [
        "https://images.pexels.com/photos/3763871/pexels-photo-3763871.jpeg",
        "https://images.pexels.com/photos/1552249/pexels-photo-1552249.jpeg",
        "https://images.pexels.com/photos/3823039/pexels-photo-3823039.jpeg"
      ]
    }

    return (
      <div className="grid grid-cols-3 gap-4">
        {images.map((img, i) => {
          const src = img || defaultImages[category][i]
          return (
            <div key={i} className="relative w-full h-40 rounded-xl overflow-hidden shadow-lg">
              <img src={src} alt={`${category}-${i}`} className="w-full h-full object-cover"/>
              <div className="absolute inset-0 bg-black/30 flex items-center justify-center text-sm text-yellow-300 opacity-0 hover:opacity-100 transition-opacity cursor-pointer">
                {img ? (
                  <button type="button" onClick={() => handleRemove(setter, i)} className="px-2 py-1 bg-red-600 rounded text-white">Remove</button>
                ) : (
                  "Click to Upload"
                )}
              </div>
              <input
                type="file"
                accept="image/*"
                onChange={e => handleUpload(e, setter, i)}
                className="absolute inset-0 opacity-0 cursor-pointer"
              />
            </div>
          )
        })}
      </div>
    )
  }

  const progressPercent = (step / totalSteps) * 100

  return (
    <div className="relative w-full min-h-screen flex items-center justify-center bg-black bg-opacity-90 backdrop-blur-lg px-4 py-10">
      <form 
        onSubmit={handleSubmit} 
        className="w-full max-w-lg md:max-w-3xl p-6 sm:p-10 rounded-2xl shadow-2xl 
                   bg-gradient-to-br from-black via-zinc-900/90 to-black border border-yellow-500/20"
      >
        {/* Progress Bar */}
        <div className="w-full bg-yellow-500/20 h-2 rounded-full mb-6">
          <motion.div 
            className="h-2 rounded-full bg-yellow-500"
            initial={{ width: 0 }}
            animate={{ width: `${progressPercent}%` }}
            transition={{ duration: 0.4 }}
          />
        </div>

        <AnimatePresence mode="wait">
          {step === 1 && (
            <motion.div key="step1" initial={{opacity:0, x:50}} animate={{opacity:1, x:0}} exit={{opacity:0, x:-50}} className="space-y-4">
              <h2 className="text-xl sm:text-2xl font-bold text-yellow-400">üë§ Personal Info</h2>
              <input type="text" placeholder="Full Name" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/>
              <input type="email" placeholder="Email" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/>
              <input type="tel" placeholder="Phone" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/>
            </motion.div>
          )}
          {step === 2 && <motion.div key="step2" initial={{opacity:0,x:50}} animate={{opacity:1,x:0}} exit={{opacity:0,x:-50}} className="space-y-4"><h2 className="text-xl sm:text-2xl font-bold text-yellow-400">üì∏ Headshots</h2><ImagePlaceholders images={headshots} setter={setHeadshots} category="headshots"/></motion.div>}
          {step === 3 && <motion.div key="step3" initial={{opacity:0,x:50}} animate={{opacity:1,x:0}} exit={{opacity:0,x:-50}} className="space-y-4"><h2 className="text-xl sm:text-2xl font-bold text-yellow-400">üîç Close-ups</h2><ImagePlaceholders images={closeUps} setter={setCloseUps} category="closeUps"/></motion.div>}
          {step === 4 && <motion.div key="step4" initial={{opacity:0,x:50}} animate={{opacity:1,x:0}} exit={{opacity:0,x:-50}} className="space-y-4"><h2 className="text-xl sm:text-2xl font-bold text-yellow-400">üßç‚Äç‚ôÄÔ∏è Full Body</h2><ImagePlaceholders images={fullBody} setter={setFullBody} category="fullBody"/></motion.div>}
          {step === 5 && <motion.div key="step5" initial={{opacity:0,x:50}} animate={{opacity:1,x:0}} exit={{opacity:0,x:-50}} className="space-y-4"><h2 className="text-xl sm:text-2xl font-bold text-yellow-400">üëñ Pants / Jeans</h2><ImagePlaceholders images={pants} setter={setPants} category="pants"/></motion.div>}
          {step === 6 && <motion.div key="step6" initial={{opacity:0,x:50}} animate={{opacity:1,x:0}} exit={{opacity:0,x:-50}} className="space-y-4"><h2 className="text-xl sm:text-2xl font-bold text-yellow-400">üèä Sportswear / Swimwear</h2><ImagePlaceholders images={sportswear} setter={setSportswear} category="sportswear"/></motion.div>}
          {step === 7 && <motion.div key="step7" initial={{opacity:0,x:50}} animate={{opacity:1,x:0}} exit={{opacity:0,x:-50}} className="space-y-4"><h2 className="text-xl sm:text-2xl font-bold text-yellow-400">üìè Measurements</h2><input type="text" placeholder="Height (cm)" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/><input type="text" placeholder="Bust / Chest (cm)" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/><input type="text" placeholder="Waist (cm)" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/><input type="text" placeholder="Hips (cm)" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/></motion.div>}
          {step === 8 && <motion.div key="step8" initial={{opacity:0,x:50}} animate={{opacity:1,x:0}} exit={{opacity:0,x:-50}} className="space-y-4"><h2 className="text-xl sm:text-2xl font-bold text-yellow-400">üåç Social Media</h2><input type="url" placeholder="Facebook" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/><input type="url" placeholder="Instagram" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/><input type="url" placeholder="TikTok" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/></motion.div>}
          {step === 9 && <motion.div key="step9" initial={{opacity:0,x:50}} animate={{opacity:1,x:0}} exit={{opacity:0,x:-50}} className="space-y-4"><h2 className="text-xl sm:text-2xl font-bold text-yellow-400">üìù Bio</h2><textarea placeholder="Tell us about yourself..." rows="4" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/></motion.div>}
          {step === 10 && <motion.div key="step10" initial={{opacity:0,x:50}} animate={{opacity:1,x:0}} exit={{opacity:0,x:-50}} className="space-y-4 text-center text-yellow-100"><h2 className="text-xl sm:text-2xl font-bold text-yellow-400">‚úÖ Review & Submit</h2><p className="text-white/80 mb-4">Please make sure all images and details are correct.</p></motion.div>}
        </AnimatePresence>

        {/* Navigation */}
        <div className="mt-6 flex justify-between">
          {step > 1 && <button type="button" onClick={() => setStep(step - 1)} className="px-4 py-2 rounded-lg bg-zinc-800 border border-yellow-500 text-yellow-400 hover:bg-zinc-700">Back</button>}
          {step < totalSteps ? <button type="button" onClick={() => setStep(step + 1)} className="px-4 py-2 rounded-lg bg-gradient-to-r from-yellow-500 to-yellow-600 text-black font-bold shadow-md hover:from-yellow-400 hover:to-yellow-500">Next</button> : <button type="submit" className="px-6 py-2 rounded-lg bg-gradient-to-r from-yellow-500 to-yellow-600 text-black font-bold shadow-md hover:from-yellow-400 hover:to-yellow-500">Submit</button>}
        </div>
      </form>

      {/* Success Toast */}
      <AnimatePresence>
        {showToast && <motion.div initial={{opacity:0, y:50}} animate={{opacity:1, y:0}} exit={{opacity:0, y:50}} transition={{duration:0.4}} className="fixed bottom-6 inset-x-0 mx-auto w-fit px-6 py-3 bg-gradient-to-r from-yellow-500/90 to-yellow-600/90 text-black font-semibold rounded-xl shadow-xl border border-yellow-400/80 backdrop-blur-lg">üéâ Application submitted successfully!</motion.div>}
      </AnimatePresence>
    </div>
  )
}
