import { useState } from "react"
import { motion, AnimatePresence } from "framer-motion"

export default function ModelApplicationForm() {
  const [step, setStep] = useState(1)
  const [showToast, setShowToast] = useState(false)

  const handleSubmit = (e) => {
    e.preventDefault()
    setShowToast(true)
    setTimeout(() => setShowToast(false), 3000) // auto-hide after 3s
  }

  return (
    <div className="relative w-full min-h-screen flex items-center justify-center bg-black bg-opacity-90 backdrop-blur-lg px-4">
      <form 
        onSubmit={handleSubmit} 
        className="w-full max-w-lg md:max-w-3xl p-6 sm:p-10 rounded-2xl shadow-2xl 
                   bg-gradient-to-br from-black via-zinc-900/90 to-black border border-yellow-500/20"
      >
        {/* Stepper Content */}
        <AnimatePresence mode="wait">
          {step === 1 && (
            <motion.div
              key="step1"
              initial={{ opacity: 0, x: 50 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: -50 }}
              transition={{ duration: 0.4 }}
              className="space-y-4"
            >
              <h2 className="text-xl sm:text-2xl font-bold text-yellow-400">👤 Personal Information</h2>
              <input type="text" placeholder="Full Name" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/>
              <input type="email" placeholder="Email" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/>
              <input type="tel" placeholder="Phone" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/>
            </motion.div>
          )}

          {step === 2 && (
            <motion.div
              key="step2"
              initial={{ opacity: 0, x: 50 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: -50 }}
              transition={{ duration: 0.4 }}
              className="space-y-4"
            >
              <h2 className="text-xl sm:text-2xl font-bold text-yellow-400">📏 Measurements</h2>
              <input type="text" placeholder="Height (cm)" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/>
              <input type="text" placeholder="Bust / Chest (cm)" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/>
              <input type="text" placeholder="Waist (cm)" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/>
              <input type="text" placeholder="Hips (cm)" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/>
            </motion.div>
          )}

          {step === 3 && (
            <motion.div
              key="step3"
              initial={{ opacity: 0, x: 50 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: -50 }}
              transition={{ duration: 0.4 }}
              className="space-y-4"
            >
              <h2 className="text-xl sm:text-2xl font-bold text-yellow-400">📸 Profile Photo</h2>
              <img 
                src="https://images.pexels.com/photos/415829/pexels-photo-415829.jpeg"
                alt="Model Placeholder"
                className="w-full rounded-xl object-cover shadow-md"
              />
            </motion.div>
          )}

          {step === 4 && (
            <motion.div
              key="step4"
              initial={{ opacity: 0, x: 50 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: -50 }}
              transition={{ duration: 0.4 }}
              className="space-y-4"
            >
              <h2 className="text-xl sm:text-2xl font-bold text-yellow-400">🌍 Social Media</h2>
              <input type="url" placeholder="Facebook Profile Link" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/>
              <input type="url" placeholder="Instagram Handle" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/>
              <input type="url" placeholder="TikTok Username" className="w-full p-3 rounded-lg bg-zinc-900/60 border border-yellow-500/30 text-white"/>
            </motion.div>
          )}

          {step === 5 && (
            <motion.div
              key="step5"
              initial={{ opacity: 0, x: 50 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: -50 }}
              transition={{ duration: 0.4 }}
              className="space-y-4"
            >
              <h2 className="text-xl sm:text-2xl font-bold text-yellow-400">✅ Review & Submit</h2>
              <p className="text-white/80">Please check your information before submitting your application.</p>
              <img 
                src="https://images.pexels.com/photos/614810/pexels-photo-614810.jpeg"
                alt="Review Placeholder"
                className="w-full rounded-xl object-cover shadow-md"
              />
            </motion.div>
          )}
        </AnimatePresence>

        {/* Navigation Buttons */}
        <div className="mt-6 flex justify-between">
          {step > 1 && (
            <button 
              type="button"
              onClick={() => setStep(step - 1)} 
              className="px-4 py-2 rounded-lg bg-zinc-800 border border-yellow-500 text-yellow-400 hover:bg-zinc-700"
            >
              Back
            </button>
          )}
          {step < 5 ? (
            <button 
              type="button"
              onClick={() => setStep(step + 1)} 
              className="px-4 py-2 rounded-lg bg-gradient-to-r from-yellow-500 to-yellow-600 text-black font-bold shadow-md hover:from-yellow-400 hover:to-yellow-500"
            >
              Next
            </button>
          ) : (
            <button 
              type="submit" 
              className="px-6 py-2 rounded-lg bg-gradient-to-r from-yellow-500 to-yellow-600 text-black font-bold shadow-md hover:from-yellow-400 hover:to-yellow-500"
            >
              Submit
            </button>
          )}
        </div>
      </form>

      {/* ✅ Success Toast (Bottom Center) */}
      <AnimatePresence>
        {showToast && (
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 50 }}
            transition={{ duration: 0.4 }}
            className="fixed bottom-6 inset-x-0 mx-auto w-fit px-6 py-3 
                       bg-gradient-to-r from-yellow-500/90 to-yellow-600/90 
                       text-black font-semibold rounded-xl shadow-xl 
                       border border-yellow-400/80 backdrop-blur-lg"
          >
            🎉 Application submitted successfully!
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  )
}
